scripts.copy:
node -e 'require("fs-extra").mkdirsSync("dist"); require("fs-extra").copySync(".", "dist", { filter: (src) => {
      // Only copy HTML files and specific directories
      const htmlFiles = src.endsWith(".html");
      const directoriesToCopy = ["Css", "Font", "Img"];
      const isDirectoryToCopy = directoriesToCopy.some(dir => src.startsWith(`./${dir}/`) || src === `./${dir}`);
      return htmlFiles || isDirectoryToCopy;
    } }); console.log("Files copied to dist:", require("fs").readdirSync("dist"))' || true
